import React, { Component } from 'react';
import logo from './assets/logo.png';
import axios from 'axios';
// import { Link } from 'react-dom';


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      usdData: [],
      gbpData: [],
      eurData: []
    };
  }

  componentDidMount() {
    this.getPrice();
  };

  getPrice = async () => {
    const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');
    const json = await response.json();
    this.setState({ usdData: json.bpi.USD });
    this.setState({ gbpData: json.bpi.GBP });
    this.setState({ eurData: json.bpi.EUR });
    // .then.catch (error => console.error(`Error: ${error}`))
  };

  _handleButtonClick = () => {
    const{usdData, gbpData, eurData} = this.state;

  }



  render() {
    const{usdData, gbpData, eurData} = this.state;
    return (
      <div className="App">
        <h1>Helloooooow</h1>
        <h1>{usdData.description}</h1>
        <h1>{gbpData.description}</h1>
        <h1>{eurData.description}</h1>
        <button onClick={this._handleButtonClick} value={usdData.description}>{usdData.description}</button>
        <button onClick={this._handleButtonClick}>{gbpData.description}</button>
        <button onClick={this._handleButtonClick}>{eurData.description}</button>
        <div>
          <select defaultValue={this.state.selectValue}
            onChange={this.handleChange}
          >
            <option value="Orange">Orange</option>
            <option value="Radish">Radish</option>
            <option value="Cherry">Cherry</option>
          </select>
          {/* <p>{message}</p> */}
        </div>
      </div>
    )
  }
}


export default App;
