class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      prices: []
    };
  }

  componentDidMount() {
    this.getPrice();
  };

  getPrice = async() =>{
    const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');
    const json = await response.json();
    this.setState({ prices: json.bpi.USD.description });
