    return (
      <div className="App">
        <h1>Helloooooow</h1>
        <h1>{this.state.prices}</h1>
        {/* <button onClick={this._handleButtonClick}>Click me</button> */}
        <a href="/about" />
      </div>
    )
  }
}


export default App;
