import React, { Component } from 'react';
import logo from './assets/logo.png';
import Container from '@material-ui/core/Container';
import OwlCarousel from 'react-owl-carousel2';
import 'react-owl-carousel2/style.css';
import './App.css';


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      usdData: [],
      gbpData: [],
      eurData: [],
      CurrentRate: 'Price',
    };
  }

  componentDidMount() {
    this.getPrice();
  };

  getPrice = async () => {
    const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');
    const json = await response.json();
    this.setState({ usdData: json.bpi.USD });
    this.setState({ gbpData: json.bpi.GBP });
    this.setState({ eurData: json.bpi.EUR });
    // .then.catch (error => console.error(`Error: ${error}`))
  };

  _counter(e) {
    const { usdData, gbpData, eurData } = this.state;
    const item = e.item.index
    if (item === 1) {
      this.setState({ CurrentRate: usdData.rate });
    }
    if (item === 2) {
      this.setState({ CurrentRate: gbpData.rate });
    }
    if (item === 3) {
      this.setState({ CurrentRate: eurData.rate });
    }
  }

  render() {
    const { CurrentRate } = this.state;

    const options = {
      items: 1,
      nav: true,
      rewind: true,
      autoplay: true
    };


    return (
      <section className="main" >
        <header className="content">
          <Container fixed>
            <OwlCarousel ref="car" options={options} events={events} >
              <div><img src="/img/fullimage1.jpg" alt="The Last of us" /></div>
              <div><img src="/img/fullimage2.jpg" alt="GTA V" /></div>
              <div><img src="/img/fullimage3.jpg" alt="Mirror Edge" /></div>
            </OwlCarousel>
            <div className="head">
              <img src={logo} className="logo" alt="logo" />
              <div className="priceBox">
                <p>{CurrentRate} </p>
              </div>
            </div>
            <div className="slide-container">
              <div className="slide-box">
                {/* <OwlCarousel
                  className='owl-theme owl-turn'
                  stagePadding={15}
                  margin={10}
                  dots={false}
                  items={1}
                  fadeOut
                  fadeIn
                  onChange={this._counter}
                >
                  <div className='item' id="USD">
                    <div className="itemBox">
                      <h4>USD</h4>
                    </div>
                  </div>
                  <div className='item' id="GBP">
                    <div className="itemBox">
                      <h4>GBP</h4>
                    </div>
                  </div>
                  <div className='item' id="EUR">
                    <div className="itemBox">
                      <h4>EUR</h4>
                    </div>
                  </div>
                </OwlCarousel> */}
              </div>
            </div>
          </Container>
        </header>
      </section>
    )
  }
}


export default App;
