import React, { Component } from 'react';
import logo from './assets/logo.png';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import OwlCarousel from 'react-owl-carousel';
import './App.css';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      usdData: [],
      gbpData: [],
      eurData: [],
      CurrentRate: 'Price',
      selectedOption: "USD"
    };
  }

  componentDidMount() {
    this.getPrice();
  };

  getPrice = async () => {
    const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');
    const json = await response.json();
    this.setState({ usdData: json.bpi.USD });
    this.setState({ gbpData: json.bpi.GBP });
    this.setState({ eurData: json.bpi.EUR });
    // .then.catch (error => console.error(`Error: ${error}`))
  };

  _handleButtonClick = (e) => {

    if (e.item.index === 1) {
      this.setState({ CurrentRate: this.state.usdData.rate });
    }
    if (e.target.value === 2) {
      this.setState({ CurrentRate: this.state.gbpData.rate });
    }
    if (e.target.value === 3) {
      this.setState({ CurrentRate: this.state.eurData.rate });
    }
    console.log(e.target.value);
  }

  _counter = (e) => {
    let item = e.item.index;

    if (e.item.index === 0) {
      this.setState({ CurrentRate: this.state.usdData.rate });
    }

    console.log(item);
  }



  render() {
    const { usdData, CurrentRate } = this.state;

    return (
      <section className="main">
        <header className="content">
          <Container fixed>
            <div className="head">
              <img src={logo} className="logo" alt="logo" />
               <div className="priceBox">
                <p>{CurrentRate} </p>
               <button value="USD" onClick={e => this._handleButtonClick(e, "value")}>{usdData.description}</button>
              </div>
            </div>
            <div className="slide-container">
              <div className="slide-box">
                <OwlCarousel
                  className='owl-theme owl-turn'
                  stagePadding={15}
                  margin={10}
                  dots={false}
                  items={1}
                  nav
                  onChanged={this._counter}
                >
                  <div className='item' value={USD} onDragged={e => this._counter(e, "value")}>
                    <div className="itemBox">
                      <h4>USD</h4>
                    </div>
                  </div>
                  <div className='item' id="GBP">
                    <div className="itemBox">
                      <h4>GBP</h4>
                    </div>
                  </div>
                  <div className='item' id="EUR">
                    <div className="itemBox">
                      <h4>EUR</h4>
                    </div>
                  </div>
                </OwlCarousel>
              </div>
            </div>
          </Container>
        </header>
      </section>
    )
  }
}


export default App;
