import React, { Component } from 'react';
import logo from './assets/logo.png';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import OwlCarousel from 'react-owl-carousel';
import './App.css';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      usdData: [],
      gbpData: [],
      eurData: [],
      CurrentRate: 'Price',
      selectedOption: "USD",
      currentSlide: ""
    };
  }

  componentDidMount() {
    this.getPrice();
  };

  getPrice = async () => {
    const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');
    const json = await response.json();
    this.setState({ usdData: json.bpi.USD });
    this.setState({ gbpData: json.bpi.GBP });
    this.setState({ eurData: json.bpi.EUR });
    // .then.catch (error => console.error(`Error: ${error}`))
  };


  render() {
    const { usdData, gbpData, eurData, CurrentRate } = this.state;

    return (
      <React.Fragment>
        {/* <CssBaseline /> */}
        {/* <Container fixed> */}
          <section id="main">
              <div class="wrapperVerticalll">
                <OwlCarousel className='owl-theme owl-turn ' loop margin={10} nav >
                  <div class='item' id="USD">
                    <h4>1</h4>
                  </div>
                  <div class='item' id="GBP">
                    <h4>2</h4>
                  </div>
                  <div class='item' id="EUR">
                    <h4>3</h4>
                  </div>
                </OwlCarousel>
              </div>
          </section>
        {/* </Container> */}
      </React.Fragment >
    )
  }
}


export default App;
