import React, { Component } from 'react';
import '../App.css';
import Slider from 'react-animated-slider';
import 'react-animated-slider/build/horizontal.css';

class SliderPage extends Component {
  constructor(props) {
    super(props)
    this.state = {
      Data: [],
      CurrentRate: 'Price',
      RateDis: 'Current Rate',
      currencyData: [],
      currentCurrency:0,
      index: 0,
    };
  }

  componentDidMount() {
    this.getPrice();

  };

  getPrice = async () => {
    const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');
    const json = await response.json();
    this.setState({ Data: json.bpi });
    const { Data, currencyData } = this.state;
    this.setState({ currencyData: Object.keys(Data) });
  };

  _counter = (e) => {
    const CurrencyName = e.slideIndex;
    this.setState({
      currentCurrency: CurrencyName
    });
    const { Data, currencyData, currentCurrency } = this.state;
    const currentSlide = currencyData[this.state.currentCurrency];
    const getValue = Data+'.'+`${currentSlide}`+'.'+ rate;
    if (CurrencyName === currentCurrency) {
    this.setState({CurrentRate: getValue})
    }
  };

  render() {

    const { CurrentRate, RateDis, Data, currencyData } = this.state;
    const currentSlide = currencyData[this.state.index];

    return (
      <div>
        <div className="priceBox">
          <h2 className="RateText">{CurrentRate}</h2>
          <p className="RateDis">{RateDis}</p>
          <p className="RateDis">{currentSlide}</p>
        </div>
        <div className="slideBox">
          <Slider onSlideChange={(e) => this._counter(e)}>
            {currencyData.map((slide, index) => {
              // this.setState({currentCurrency: slide})
              return (
                <div key={index}>
                  <div className="itemBox">
                    <h4>{slide}</h4>
                  </div>
                </div>)
            })}
          </Slider>
        </div>
      </div>

    )
  }
}


export default SliderPage;
