import React, { Component } from 'react';
import '../App.css';
import Slider from 'react-animated-slider';
import 'react-animated-slider/build/horizontal.css';
import horizontalCss from 'react-animated-slider/build/horizontal.css';

class SliderPage extends Component {
  constructor(props) {
    super(props)
    this.state = {
      usdData: [],
      gbpData: [],
      eurData: [],
      CurrentRate: 'Price',
    };
  }

  componentDidMount() {
    this.getPrice();
  };

  getPrice = async () => {
    const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');
    const json = await response.json();
    this.setState({ usdData: json.bpi.USD });
    this.setState({ gbpData: json.bpi.GBP });
    this.setState({ eurData: json.bpi.EUR });
  };

  _counter = (e) => {
    const index = e.slideIndex;

    if (index === 1) {
      this.setState({ CurrentRate: this.state.usdData.rate });
    }
    if (index === 2) {
      this.setState({ CurrentRate: this.state.gbpData.rate });
    }
    if (index === 3) {
      this.setState({ CurrentRate: this.state.eurData.rate });
    }

    console.log(index);
  };

  render() {

    const { CurrentRate } = this.state;
    const slides = [
      { title: 'Swipe'},
      { title: 'USA'},
      { title: 'GBP'},
      { title: 'EUR'}
    ];

    return (
      <div>
        <div className="priceBox">
          <h2>{CurrentRate}</h2>
        </div>
        <div className="slideBox">
        <Slider onSlideChange={(e) => this._counter(e)}>
          {slides.map((slide, index) => {
            return (
              <div key={index}>
                <h2>{slide.title}</h2>
                <div>{slide.description}</div>
              </div>)})}
        </Slider>
        </div>
      </div>

    )
  }
}


export default SliderPage;
