import React, { useState, useEffect } from 'react';
import logo from './assets/logo.png';
import Container from '@material-ui/core/Container';
import OwlCarousel from 'react-owl-carousel';
import './App.css';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';


const App = () => {

  const{UsdData, setUsdData} = useState([]);
  const{gbpData, setGbpData} = useState([]);
  const{eurData, setEurData} = useState([]);
  const{CurrentRate, setCurrentRate} = useState('price');
  const{currentPrice, setcurrentPrice} = useState('');



  useEffect(()=>{

    getPrice = async () => {
      const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');
      const json = await response.json();
      this.setState({ usdData: json.bpi.USD });
      this.setState({ gbpData: json.bpi.GBP });
      this.setState({ eurData: json.bpi.EUR });
      // .then.catch (error => console.error(`Error: ${error}`))
    };

}, [])




  _counter = (e) => {
    const index = e.item.index;
    // if (index > 0) {
    //   this.setState({ currentPrice: index });
    // }

    if (index === 1) {
      this.setState({ CurrentRate: this.state.usdData.rate });
    }
    if (index === 2) {
      this.setState({ CurrentRate: this.state.gbpData.rate });
    }
    if (index === 3) {
      this.setState({ CurrentRate: this.state.eurData.rate });
    }

    console.log(index);
  };


    return (
      <section className="main">
        <header className="content">
          <Container fixed>
            <div className="head">
              <img src={logo} className="logo" alt="logo" />
              <div className="priceBox">
                <p>{CurrentRate} </p>
                <h1>{currentPrice}</h1>
              </div>
            </div>
            <div className="slide-container">
              <div className="slide-box">
                <OwlCarousel
                  className='owl-theme owl-turn'
                  stagePadding={25}
                  margin={5}
                  dots={false}
                  items={1}
                  autoWidth={false}
                  center={false}
                  loop
                  nav
                  onChanged={(e) => this._counter(e)}
                >
                  <div className='item'>
                    <div className="itemBox">
                      <h4>Swipe Up</h4>
                    </div>
                  </div>
                  <div className='item'>
                    <div className="itemBox">
                      <h4>USD</h4>
                    </div>
                  </div>
                  <div className='item'>
                    <div className="itemBox">
                      <h4>GBP</h4>
                    </div>
                  </div>
                  <div className='item'>
                    <div className="itemBox">
                      <h4>EUR</h4>
                    </div>
                  </div>
                </OwlCarousel>
              </div>
            </div>
          </Container>
        </header>
      </section>
    )
}



export default App;
