import React, { Component } from 'react';
import logo from './assets/logo.png';
import axios from 'axios';
import SmoothPicker from "react-picker";
// import { Link } from 'react-dom';

const dataCity = [
  'Paris',
  'Berlin',
  'Lisbonne',
  'Budapest',
  'Londres',
  'Prague',
  'Rome',
  'Barcelone',
  'Amsterdam',
  'Dublin',
  'Vienne',
  'Madrid',
  'Cracovie',
  'Reykjavik',
  'Istambul',
  'Florence',
  'Copenhague',
  'Zagreb',
  'Stockholm',
  'Thessalonique',
  'Marseille',
  'Porto',
  'Lugano',
  'Bruxelles',
  'Lyon',
];

const opacities = {
  0: 1,
  1: 1,
  2: 0.6,
  3: 0.3,
  4: 0.1,
};
const sizeText = {
  0: 20,
  1: 15,
  2: 10,
};

const Item = React.memo(({opacity, selected, vertical, fontSize, name}) => {
  return (
    <View
      style={[styles.OptionWrapper, { opacity, borderColor: selected ? '#ABC9AF' : 'transparent', width: vertical ? 190 : 'auto'}]}
    >
    <Text style={{fontSize}}>
      {name}
    </Text>
  </View>
  );
});




const ItemToRender = ({item, index}, indexSelected, vertical) => {
  const selected = index === indexSelected;
  const gap = Math.abs(index - indexSelected);

  let opacity = opacities[gap];
  if (gap > 3) {
    opacity = opacities[4];
  }
  let fontSize = sizeText[gap];
  if (gap > 1) {
    fontSize = sizeText[2];
  }

  return <Item opacity={opacity} selected={selected} vertical={vertical} fontSize={fontSize} name={item}/>;
};

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      usdData: [],
      gbpData: [],
      eurData: [],
      CurrentRate:'Price'
    };
  }

  componentDidMount() {
    this.getPrice();
  };

  getPrice = async () => {
    const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');
    const json = await response.json();
    this.setState({ usdData: json.bpi.USD });
    this.setState({ gbpData: json.bpi.GBP });
    this.setState({ eurData: json.bpi.EUR });
    // .then.catch (error => console.error(`Error: ${error}`))
  };

  _handleButtonClick = (e) => {
    const{usdData, gbpData, eurData} = this.state;

    if(e.target.value === "USD") {
      this.setState({ CurrentRate: usdData.rate});
    }
    if(e.target.value === "GBP") {
      this.setState({ CurrentRate: gbpData.rate});
    }
    if(e.target.value === "EUR") {
      this.setState({ CurrentRate: eurData.rate});
    }
    console.log(e.target.value);
  }



  render() {
    const{usdData, gbpData, eurData, CurrentRate} = this.state;
    return (
      <div className="App">
        <h1>Helloooooow</h1>
        <h1>{usdData.description}</h1>
        <h1>{gbpData.description}</h1>
        <h1>{eurData.description}</h1>
        <button value="USD" onClick={e => this._handleButtonClick(e, "value")}>{usdData.description}</button>
        <button value="GBP" onClick={e => this._handleButtonClick(e, "value")}>{gbpData.description}</button>
        <button value="EUR" onClick={e => this._handleButtonClick(e, "value")}>{eurData.description}</button>
        <p>{CurrentRate}</p>
        <div>
          <select defaultValue={this.state.selectValue}
            onChange={this.handleChange}
          >
            <option value="Orange">Orange</option>
            <option value="Radish">Radish</option>
            <option value="Cherry">Cherry</option>
          </select>
          {/* <p>{message}</p> */}
        </div>
      </div>
    )
  }
}


export default App;
