import React, { Component } from 'react';
import logo from './assets/logo.png';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import OwlCarousel from 'react-owl-carousel';
import './App.css';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      usdData: [],
      gbpData: [],
      eurData: [],
      CurrentRate: 'Price',
    };
  }

  componentDidMount() {
    this.getPrice();
  };

  getPrice = async () => {
    const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');
    const json = await response.json();
    this.setState({ usdData: json.bpi.USD });
    this.setState({ gbpData: json.bpi.GBP });
    this.setState({ eurData: json.bpi.EUR });
    // .then.catch (error => console.error(`Error: ${error}`))
  };


  render() {
    const { usdData, gbpData, eurData, CurrentRate } = this.state;

    return (
      <section className="main">
        <Container fixed>
          <div className="content">
            <div className="App">
              <img src={logo} className="logo" alt="logo" />
              <p>{CurrentRate} </p>
            </div>
            <div className="slide-container">
              <div className="slide-box">
                <OwlCarousel className='owl-theme owl-turn' stagePadding={15} margin={10} dots={false} items={1}>
                  <div className='item' id="USD">
                    <div className="itemBox">
                      <h4>USD</h4>
                    </div>
                  </div>
                  <div className='item' id="GBP">
                    <div className="itemBox">
                      <h4>GBP</h4>
                    </div>
                  </div>
                  <div className='item' id="EUR">
                    <div className="itemBox">
                      <h4>EUR</h4>
                    </div>
                  </div>
                </OwlCarousel>
              </div>
            </div>
          </div>
        </Container>
      </section>
    )
  }
}


export default App;
