import React, { Component } from 'react';
import logo from './assets/logo.png';
import axios from 'axios';
// import { Link } from 'react-dom';


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      usdData: [],
      gbpData: [],
      eurData: [],
      CurrentRate: 'Price',
      selectValue: ""
    };
  }

  componentDidMount() {
    this.getPrice();
  };

  getPrice = async () => {
    const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');
    const json = await response.json();
    this.setState({ usdData: json.bpi.USD });
    this.setState({ gbpData: json.bpi.GBP });
    this.setState({ eurData: json.bpi.EUR });
    // .then.catch (error => console.error(`Error: ${error}`))
  };

  _handleButtonClick = (e) => {
    const { usdData, gbpData, eurData } = this.state;

    if (e.target.value === "USD") {
      this.setState({ CurrentRate: usdData.rate });
    }
    if (e.target.value === "GBP") {
      this.setState({ CurrentRate: gbpData.rate });
    }
    if (e.target.value === "EUR") {
      this.setState({ CurrentRate: eurData.rate });
    }
    console.log(e.target.value);
  }

  handleChange = (e) => {
    this.setState({ selectValue: e.target.value });
  }

  handleClick = () => {
    this.setState({ selectValue: "EUR" });
  }



  render() {
    const { usdData, gbpData, eurData, CurrentRate } = this.state;
    return (
      <div className="App">
        <h1>Helloooooow</h1>
        <h1>{usdData.description}</h1>
        <h1>{gbpData.description}</h1>
        <h1>{eurData.description}</h1>
        <button value="USD" onClick={e => this._handleButtonClick(e, "value")}>{usdData.description}</button>
        <button value="GBP" onClick={e => this._handleButtonClick(e, "value")}>{gbpData.description}</button>
        <button value="EUR" onClick={e => this._handleButtonClick(e, "value")}>{eurData.description}</button>
        <p>{CurrentRate}</p>
        <div>
          <select defaultValue={this.state.selectValue} onChange={this.handleChange}>
            <option value="USD">USD</option>
            <option value="GBP">GBP</option>
            <option value="EUR">EUR</option>
          </select>
          <button onClick={this.handleClick}>Click Me</button>
          {/* <p>{message}</p> */}
        </div>
      </div>
    )
  }
}


export default App;
