import React, { Component } from 'react';
import '../App.css';
import Slider from 'react-animated-slider';
import 'react-animated-slider/build/horizontal.css';

class SliderPage extends Component {
  constructor(props) {
    super(props)
    this.state = {
      usdData: [],
      gbpData: [],
      eurData: [],
      Data: [],
      CurrentRate: 'Price',
      RateDis: 'Current Rate',
      currencyData: [],
    };
  }

  componentDidMount() {
    this.getPrice();

  };

  getPrice = async () => {
    const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');
    const json = await response.json();
    this.setState({ Data: json.bpi });

    const { Data } = this.state;

    let apiDataArray = Object.keys(Data);
    let itemList = [];
    let data = new Object();
    apiDataArray.map((item, i) => {
      data['code'] = item;
    });
    itemList = [...itemList, data]
    console.log('-- API Data :: ' + JSON.stringify(itemList));
    this.setState({ currencyData: Object.keys(Data) });
    // console.log(this.state.Data)
  };

  _counter = (e) => {
    const index = e.slideIndex;

    const { Data } = this.state;

    if (index === 0) {
      this.setState({ CurrentRate: 'Price' });
      this.setState({ RateDis: 'Current Rate' });
    }
    if (index === 1) {
      this.setState({ CurrentRate: this.state.usdData.rate });
      this.setState({ RateDis: this.state.usdData.description });
    }
    if (index === 2) {
      this.setState({ CurrentRate: this.state.gbpData.rate });
      this.setState({ RateDis: this.state.gbpData.description });
    }
    if (index === 3) {
      this.setState({ CurrentRate: this.state.eurData.rate });
      this.setState({ RateDis: this.state.eurData.description });
    }

    console.log('-- API Data :: ' + JSON.stringify(Object.keys(this.state.Data)));

  };

  render() {

    const { CurrentRate, RateDis, Data, currencyData } = this.state;
    const slides = [
      { title: 'Swipe', Value: 0 },
      { title: 'USA', Value: 1 },
      { title: 'GBP', Value: 2},
      { title: 'EUR', Value: 3 }
    ];

    return (
      <div>
        <div className="priceBox">
          {
            currencyData.map((item, i) => {
              return(<h4>{item}</h4>)
            })
          }


        <h4>{JSON.stringify(Object.keys(Data))}</h4>

          {/* <h2 className="RateText">{CurrentRate}</h2>
          <p className="RateDis">{RateDis}</p> */}
        </div>
        <div className="slideBox">
          <Slider onSlideChange={(e) => this._counter(e)}>
            {currencyData.map((slide, index) => {
              return (
                <div key={index}>
                  <div className="itemBox">
                    <h4>{slide}</h4>
                  </div>
                </div>)
            })}
          </Slider>
        </div>
      </div>

    )
  }
}


export default SliderPage;
