import React, { Component } from 'react';
import logo from './assets/logo.png';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import OwlCarousel from 'react-owl-carousel';
import './App.css';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      usdData: [],
      gbpData: [],
      eurData: [],
      CurrentRate: 'Price',
      selectedOption: "USD",
      currentSlide: ""
    };
  }

  componentDidMount() {
    this.getPrice();
  };

  getPrice = async () => {
    const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');
    const json = await response.json();
    this.setState({ usdData: json.bpi.USD });
    this.setState({ gbpData: json.bpi.GBP });
    this.setState({ eurData: json.bpi.EUR });
    // .then.catch (error => console.error(`Error: ${error}`))
  };


  render() {
    const { usdData, gbpData, eurData, CurrentRate } = this.state;

    return (
      <section className="main">
        <header className="App-header">
          <Container fixed>
            <div className="App">

              <img src={logo} className="logo" alt="logo" />
              <p>Edit <code>src/App.js</code> and save to reload. </p>
              <a
                className="App-link"
                href="https://reactjs.org"
                target="_blank"
                rel="noopener noreferrer"
              >
                Learn React</a>
            </div>
            <div className="slide-container">
            <OwlCarousel className='owl-theme owl-turn' loop >
                <div className='item' id="USD">
                  <h4>USD</h4>
                </div>
                <div className='item' id="GBP">
                  <h4>GBP</h4>
                </div>
                <div className='item' id="EUR">
                  <h4>EUR</h4>
                </div>
                <div className='item' id="EUR">
                  <h4>EUR</h4>
                </div>
                <div className='item' id="EUR">
                  <h4>EUR</h4>
                </div>
                <div className='item' id="EUR">
                  <h4>EUR</h4>
                </div>
                <div className='item' id="EUR">
                  <h4>EUR</h4>
                </div>
                <div className='item' id="EUR">
                  <h4>EUR</h4>
                </div>
                <div className='item' id="EUR">
                  <h4>EUR</h4>
                </div>
              </OwlCarousel>
              </div>
          </Container>
        </header>
      </section>
    )
  }
}


export default App;
