import React, { Component } from 'react';
import logo from './assets/logo.png';
import axios from 'axios';
import OwlCarousel from 'react-owl-carousel';
import './App.css';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      usdData: [],
      gbpData: [],
      eurData: [],
      CurrentRate: 'Price',
      selectedOption: "USD"
    };
  }

  componentDidMount() {
    this.getPrice();
  };

  getPrice = async () => {
    const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');
    const json = await response.json();
    this.setState({ usdData: json.bpi.USD });
    this.setState({ gbpData: json.bpi.GBP });
    this.setState({ eurData: json.bpi.EUR });
    // .then.catch (error => console.error(`Error: ${error}`))
  };

  _handleButtonClick = (e) => {
    const { usdData, gbpData, eurData } = this.state;

    if (e.target.value === "USD") {
      this.setState({ CurrentRate: usdData.rate });
    }
    if (e.target.value === "GBP") {
      this.setState({ CurrentRate: gbpData.rate });
    }
    if (e.target.value === "EUR") {
      this.setState({ CurrentRate: eurData.rate });
    }
    console.log(e.target.value);
  }

  handleChange = (e) => {
    this.setState({ selectedOption: e.target.value });
  }

  _handleClickUp = () => {
    const { selectedOption } = this.state;
    if (selectedOption === "USD") {
      this.setState({ selectedOption: "USD" });
    }
    if (selectedOption === "GBP") {
      this.setState({ selectedOption: "USD" });
    }
    if (selectedOption === "EUR") {
      this.setState({ selectedOption: "GBP" });
    }
  }

  _handleClickDown = () => {
    const { selectedOption } = this.state;
    if (selectedOption === "USD") {
      this.setState({ selectedOption: "GBP" });
    }
    if (selectedOption === "GBP") {
      this.setState({ selectedOption: "EUR" });
    }
    if (selectedOption === "EUR") {
      this.setState({ selectedOption: "EUR" });
    }
  }



  render() {
    const { usdData, gbpData, eurData, CurrentRate } = this.state;
    return (
      <div className="App">
        <h1>Helloooooow</h1>
        <h1>{usdData.description}</h1>
        <h1>{gbpData.description}</h1>
        <h1>{eurData.description}</h1>
        <button value="USD" onClick={e => this._handleButtonClick(e, "value")}>{usdData.description}</button>
        <button value="GBP" onClick={e => this._handleButtonClick(e, "value")}>{gbpData.description}</button>
        <button value="EUR" onClick={e => this._handleButtonClick(e, "value")}>{eurData.description}</button>
        <p>{CurrentRate}</p>
        <div>
          <select value={this.state.selectedOption} onChange={this.handleChange}>
            <option value="USD">USD</option>
            <option value="GBP">GBP</option>
            <option value="EUR">EUR</option>
          </select>
          <button onClick={this._handleClickUp}>UP Click Me</button>
          <button onClick={this._handleClickDown}>Down Click Me</button>
          {/* <p>{message}</p> */}
        </div>

        <OwlCarousel className='owl-theme owl-turn' loop margin={10} nav>
          <div class='item'>
            <h4>1</h4>
          </div>
          <div class='item'>
            <h4>2</h4>
          </div>
          <div class='item'>
            <h4>3</h4>
          </div>
        </OwlCarousel>
      </div>
    )
  }
}


export default App;
