import React, { Component } from 'react';
import logo from './assets/logo.png';
import axios from 'axios';
// import { Link } from 'react-dom';


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      usdData: [],
      gbpData: [],
      eurData: [],
      CurrentRate: 'Price',
      data: [{
        value: 1,
        text: 'Atlanta'
      }, {
        value: 2,
        text: 'Berlin'
      }, {
        value: 3,
        text: 'Boston'
      }, {
        value: 4,
        text: 'Chicago'
      }, {
        value: 5,
        text: 'London'
      }, {
        value: 6,
        text: 'Los Angeles'
      }, {
        value: 7,
        text: 'New York'
      }, {
        value: 8,
        text: 'Paris'
      }, {
        value: 9,
        text: 'San Francisco'
      }],
      value: 1,
      header: 1,
      nonform: 1,
      external: 1
    };
  }

  show = () => {
    this.external.instance.show();
  }

  setExternal = (comp) => {
    this.external = comp;
  }

  componentDidMount() {
    this.getPrice();
  };

  getPrice = async () => {
    const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');
    const json = await response.json();
    this.setState({ usdData: json.bpi.USD });
    this.setState({ gbpData: json.bpi.GBP });
    this.setState({ eurData: json.bpi.EUR });
    // .then.catch (error => console.error(`Error: ${error}`))
  };

  _handleButtonClick = (e) => {
    const { usdData, gbpData, eurData } = this.state;

    if (e.target.value === "USD") {
      this.setState({ CurrentRate: usdData.rate });
    }
    if (e.target.value === "GBP") {
      this.setState({ CurrentRate: gbpData.rate });
    }
    if (e.target.value === "EUR") {
      this.setState({ CurrentRate: eurData.rate });
    }
    console.log(e.target.value);
  }



  render() {
    const { usdData, gbpData, eurData, CurrentRate } = this.state;
    return (
      <div className="App">
        <h1>Helloooooow</h1>
        <h1>{usdData.description}</h1>
        <h1>{gbpData.description}</h1>
        <h1>{eurData.description}</h1>
        <button value="USD" onClick={e => this._handleButtonClick(e, "value")}>{usdData.description}</button>
        <button value="GBP" onClick={e => this._handleButtonClick(e, "value")}>{gbpData.description}</button>
        <button value="EUR" onClick={e => this._handleButtonClick(e, "value")}>{eurData.description}</button>
        <p>{CurrentRate}</p>
        <div>
          <select defaultValue={this.state.selectValue}
            onChange={this.handleChange}
          >
            <option value="Orange">Orange</option>
            <option value="Radish">Radish</option>
            <option value="Cherry">Cherry</option>
          </select>
          {/* <p>{message}</p> */}
        </div>

        <div>
          <mobiscroll.Form inputStyle="box">
            <div className="mbsc-grid mbsc-form-grid">
              <mobiscroll.FormGroup>
                <mobiscroll.FormGroupTitle>Using the Select with Mobiscroll form fields</mobiscroll.FormGroupTitle>
                <div className="mbsc-row">
                  <div className="mbsc-col-sm-12 mbsc-col-md-6">
                    <mobiscroll.Select display="bubble" value={this.state.value} data={this.state.data}>
                      <mobiscroll.Input inputStyle="box">Select</mobiscroll.Input>
                    </mobiscroll.Select>
                  </div>
                  <div className="mbsc-col-sm-12 mbsc-col-md-6">
                    <mobiscroll.Select display="bubble" value={this.state.header} data={this.state.data} headerText="Pick location">
                      <mobiscroll.Input inputStyle="box">Header text</mobiscroll.Input>
                    </mobiscroll.Select>
                  </div>
                </div>
              </mobiscroll.FormGroup>
            </div>
          </mobiscroll.Form>
          <div className="mbsc-grid">
            <div className="mbsc-row">
              <div className="mbsc-col-sm-12 mbsc-col-md-6">
                <label htmlFor="demo-non-form">Select</label>
                <mobiscroll.Select
                  display="bubble"                    // Specify display mode like: display="bottom" or omit setting to use default
                  value={this.state.nonform}
                  data={this.state.data}              // More info about data: https://docs.mobiscroll.com/4-10-8/react/select#opt-data
                  inputClass="demo-non-form"          // More info about inputClass: https://docs.mobiscroll.com/4-10-8/react/select#opt-inputClass
                />
              </div>
              <div className="mbsc-col-sm-12 mbsc-col-md-6">
                <div className="mbsc-row mbsc-align-items-end">
                  <div className="mbsc-col-9">
                    <label htmlFor="demo-external">Show on button click only</label>
                    <mobiscroll.Select
                      ref={this.setExternal}
                      display="bubble"                    // Specify display mode like: display="bottom" or omit setting to use default
                      value={this.state.external}
                      data={this.state.data}              // More info about data: https://docs.mobiscroll.com/4-10-8/react/select#opt-data
                      inputClass="demo-non-form"          // More info about inputClass: https://docs.mobiscroll.com/4-10-8/react/select#opt-inputClass
                      showOnTap={false}           // More info about showOnTap: https://docs.mobiscroll.com/4-10-8/react/select#opt-showOnTap
                      showOnFocus={false}         // More info about showOnFocus: https://docs.mobiscroll.com/4-10-8/react/select#opt-showOnFocus
                    />
                  </div>
                  <div className="mbsc-col-3 external-container">
                    <button onClick={this.show} className="external-button">Show</button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}


export default App;
